import React, { useState, useEffect } from 'react';
     import axios from 'axios';
     import './App.css';

     function App() {
       const [messages, setMessages] = useState([]);
       const [input, setInput] = useState('');

       useEffect(() => {
         // Scroll to the latest message
         const chatWindow = document.querySelector('.chat-window');
         chatWindow.scrollTop = chatWindow.scrollHeight;
       }, [messages]);

       const handleSend = async () => {
         if (!input.trim()) return;

         const userMessage = { text: input, sender: 'user' };
         setMessages([...messages, userMessage]);
         setInput('');

         try {
           const response = await axios.post('http://localhost:5000/api/chat', { message: input });
           setMessages(prev => [...prev, { text: response.data.response, sender: 'bot' }]);
         } catch (error) {
           console.error('Error fetching bot response:', error);
           setMessages(prev => [...prev, { text: 'Sorry, something went wrong.', sender: 'bot' }]);
         }
       };

       const handleKeyPress = (e) => {
         if (e.key === 'Enter') handleSend();
       };

       return (
         <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
           <div className="w-full max-w-md bg-white rounded-xl shadow-2xl p-6">
             <h1 className="text-3xl font-bold text-center text-blue-600 mb-6">BUddy: Your AI Guide</h1>
             <div className="h-96 overflow-y-auto mb-6 p-4 bg-gray-50 rounded-lg border border-gray-200 chat-window">
               {messages.map((msg, index) => (
                 <div key={index} className={`mb-4 ${msg.sender === 'user' ? 'text-right' : 'text-left'}`}>
                   <div className={`inline-block p-3 rounded-lg ${msg.sender === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'}`}>
                     {msg.text}
                   </div>
                 </div>
               ))}
             </div>
             <div className="flex space-x-2">
               <input
                 type="text"
                 value={input}
                 onChange={(e) => setInput(e.target.value)}
                 onKeyPress={handleKeyPress}
                 placeholder="Ask me anything about HKBU..."
                 className="flex-1 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
               />
               <button
                 onClick={handleSend}
                 className="bg-blue-600 text-white p-3 rounded-lg hover:bg-blue-700 transition duration-200"
               >
                 Send
               </button>
             </div>
           </div>
         </div>
       );
     }

     export default App;
