import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const chatWindowRef = useRef(null);

  useEffect(() => {
    if (chatWindowRef.current) {
      chatWindowRef.current.scrollTop = chatWindowRef.current.scrollHeight;
    }
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: 'user' };
    setMessages([...messages, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/api/chat', { message: input });
      setMessages(prev => [...prev, { text: response.data.response, sender: 'bot' }]);
    } catch (error) {
      console.error('Error fetching bot response:', error);
      setMessages(prev => [...prev, { text: `Sorry, something went wrong: ${error.message || 'Unknown error'}`, sender: 'bot' }]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    // Send message on Enter, insert new line on Ctrl + Enter or Shift + Enter
    if (e.key === 'Enter' && !e.ctrlKey && !e.shiftKey && e.target.value.trim()) {
      e.preventDefault(); // Prevent default form submission
      handleSend();
    } else if (e.key === 'Enter' && (e.ctrlKey || e.shiftKey)) {
      e.preventDefault(); // Prevent default form submission
      setInput(input + '\n'); // Add a new line
    }
  };

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  return (
    <div className="app-container">
      <button
        aria-label="Toggle sidebar"
        className="sidebar-toggle"
        onClick={toggleSidebar}
      >
        â˜°
      </button>
      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        <h2>Settings</h2>
        <ul>
          <li>
            Theme: 
            <select value="light" onChange={(e) => console.log(e.target.value)}>
              <option value="light">Light</option>
              <option value="dark">Dark</option>
            </select>
          </li>
          <li>Model: Llama-3.1</li>
        </ul>
      </div>
      <div className="chat-container">
        <h1 className="app-header">BUddy: Your AI Guide</h1>
        <div className="chat-window" ref={chatWindowRef}>
          {messages.map((msg, index) => (
            <div key={index} className={`message ${msg.sender}`}>
              <div className="message-bubble">
                {msg.text.split('\n').map((line, i) => (
                  <div key={i}>{line}</div> // Render each line as a new div for proper display
                ))}
              </div>
            </div>
          ))}
          {isLoading && <div className="loading">Thinking...</div>}
        </div>
        <div className="input-area">
          <textarea
            aria-label="Chat input"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Ask me anything about HKBU... (Press Ctrl + Enter or Shift + Enter for new line)"
            rows="3"
            style={{ resize: 'vertical' }}
          />
          <button onClick={handleSend} aria-label="Send message">
            Send
            <img src="Send.png" alt="Send Icon" className="button-icon" />
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
